import { useContext } from 'react';
import { AppContext } from "./../context/AppContext";
import Link from 'next/link';

const CartIcon = () => {

	const [cart] = useContext(AppContext);

	const productsCount = 0;
	//(null !== cart && Object.keys(cart).length) ? cart.totalProductsCount : '';
	const totalPrice = 0;
	//(null !== cart && Object.keys(cart).length) ? cart.totalProductsPrice : '';

	console.log(cart);
	if ((cart !== undefined) && (cart !== null)){
		console.log(Object.keys(cart));
		const productsCount = cart.totalProductsCount;
		const totalPrice = cart.totalProductsPrice;
	}else {
		const productsCount = '';
		const totalPrice = '';

	}

	console.log('productsCount : ',productsCount);

	return (
		<>
			<Link href="/cart">
				<a>
					<div className="woo-next-cart-wrap">
						{ totalPrice ? <span className="woo-next-cart-price mr-2">{ totalPrice }</span> : '' }
						<span className="woo-next-cart-icon-container">
							<i className="fa fa-shopping-cart woo-next-cart-icon"/>
							{ productsCount ? <span className="woo-next-cart-count">{ productsCount }</span> : '' }
						</span>
					</div>
				</a>
			</Link>
		</>

	)
};

export default CartIcon;
