import Link from 'next/link';
import { useContext, useState, useEffect } from 'react';
import { AppContext } from "../../context/AppContext";
import CartItem from "./CartItem";

const CartItemsContainer = () => {
    const [cart, setCart] = useContext(AppContext);

    console.warn(cart);

	const handleRemoveProductClick = ( event, cartKey, products ) => {

		event.stopPropagation();
		if ( products.length ) {

			// By passing the newQty to 0 in updateCart Mutation, it will remove the item.
			const newQty = 0;
			const updatedItems = getUpdatedItems( products, newQty, cartKey );

			updateCart( {
				variables: {
					input: {
						clientMutationId: v4(),
						items: updatedItems
					}
				},
			} );
		}
	};


    return (
        <div>
            //Check if the cart is not empty otherwise empty string
            { cart ? (
                <div className="woo-next-cart-wrapper">
                    <h1 className="woo-next-cart-heading"></h1>
                    <table calssName="table table-hover">
                        <thead>
                            <tr className="woo-next-cart-head-container">
                                <th className="woo-next-cart-heading" scope="col" />
                                <th className="woo-next-cart-heading" scope="col" />
                                <th className="woo-next-cart-heading" scope="col">Produit</th>
                                <th className="woo-next-cart-heading" scope="col">Prix</th>
                                <th className="woo-next-cart-heading" scope="col">Quantit√©</th>
                                <th className="woo-next-cart-heading" scope="col">Total</th>
                            </tr>
                        </thead>
                        <tbody>
                            { cart.products.length && (cart.products.map( item = (
                                <CartItem
                                    key = { item.productId }
                                    item = { item }
                                    setCart={setCart}
                                    handleRemoveProductClick={ handleRemoveProductClick }
                                />
                                ))
                            )};
                        </tbody>

                    </table>
@
                </div>
            ) : ''}
        </div>
    )
};

export default CartItemsContainer;
