/** @format */

import { useContext } from "react";
import { AppContext } from "../context/AppContext";
import Link from "next/link";

const CartIcon = () => {
	//on récupère les valeurs depuis le context
	const [cart] = useContext(AppContext);
	const productsCount = 10;
	//null !== cart && Object.keys(cart).length ? cart.totalProductsCount : "";
	const totalPrice = 50;
	//null !== cart && Object.keys(cart).length ? cart.totalProductsPrice : "";

	return (
		<React.Fragment>
			<Link href='/cart'>
				<Link href='/cart'>
					<a>
						<div className='woo-next-cart-wrap'>
							{totalPrice ? (
								<span className='woo-next-cart-price mr-2'>{totalPrice}</span>
							) : (
								""
							)}
							<span className='woo-next-cart-icon-container'>
								<i className='fa fa-shopping-cart woo-next-cart-icon' />
								{productsCount ? (
									<span className='woo-next-cart-count'>{productsCount}</span>
								) : (
									""
								)}
							</span>
						</div>
					</a>
				</Link>
			</Link>
		</React.Fragment>
	);
};

export default CartIcon;
