import { stripIgnoredCharacters } from 'graphql';
import { useState } from 'react';
import { addFirstProduct, updateCart, removeItemFromCart } from "../../../functions";

const CartItem = ( {
	item,
	setCart,
	handleRemoveProductClick,

}) => {

	const [productCount, setProductCount] = useState( item.qty );

	/*
	 * When user changes the qty from product input update the cart in localStorage
	 * Also update the cart in global context
	 *
	 * @param {Object} event event
	 *
	 * @return {void}
	 */
	const handleQtyChange = (event) => {

		if ( process.browser ) {

			const newQty = event.target.value;

			setProductCount(newQty);

			console.warn('newQty:', newQty);

			let existingCart = localStorage.getItem('woo-next-cart');
			existingCart = JSON.parse(existingCart);

			const updatedCart = updateCart(existingCart, item, false, newQty);

			setCart(updatedCart);
		}
	};


	return (
		<tr className="woo-next-cart-item" key={item.productId}>

			<th className="woo-next-cart-element woo-next-cart-el-close">
				{/* Remove item */}
				<span className="woo-next-cart-close-icon"
				      onClick={ ( event ) => handleRemoveProductClick( event, item.productID ) }>
					<i className="fa fa-times-circle"/>
				</span>
			</th>

			<td className="woo-next-cart-element">
				<img width="64" src={ item.image.sourceUrl } srcSet={ item.image.srcSet } alt={ item.image.title }/>
			</td>
			<td className="woo-next-cart-element">{ item.name }</td>
			<td className="woo-next-cart-element">{ ( 'string' !== typeof item.price ) ? item.price : item.price }</td>

			{/* Qty Input */ }
			<td className="woo-next-cart-element woo-next-cart-qty">
				{/* @TODO Need to update this with graphQL query */ }
				<input
					type="number"
					min="1"
					className="woo-next-cart-qty-input"
					value={ productCount }
					onChange={ ( event ) => handleQtyChange( event ) }
				/>
			</td>
			<td className="woo-next-cart-element">{ item.totalPrice  }</td>
		</tr>
	)
};

export default CartItem;
